mapper.select:
SELECT MAX(board_num)
FROM board
WHERE user_id = #{userId}

SELECT * FROM ( SELECT ROWNUM AS rn, blist.* FROM ( SELECT board.board_num, board.user_id, board_title, board_category, board_view,board_recom, board_date, board_thum, COUNT(reply.reply_num) AS reply_cnt FROM board LEFT OUTER JOIN reply ON board.board_num = reply.board_num
GROUP BY board.board_num,board.board_num, board.user_id, board_title, board_category, board_view, board_recom, board_date, board_thum ORDER BY board_num DESC ) blist )
WHERE rn > (#{pageNum} - 1) * #{countPerPage} AND rn <= #{pageNum} * #{countPerPage}

mapper.update:
UPDATE board SET board_content = #{boardContent}, board_title = #{boardTitle}, board_category = #{boardCategory},
board_thum = #{boardThum},
board_thum = NULL,
board_video = #{boardVideo}
board_video = NULL
WHERE board_num = #{boardNum}
